import React from 'react';
import AddIcon from '@material-ui/icons/Add';
import DeleteForeverIcon from '@material-ui/icons/Delete';
import Button from '@material-ui/core/Button';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
const CreateNote = (props) =>{
    const [expand, setExpand] = useState(false);
    const [note, setNote] = useState({
        title: "",
        content: ""
    });
    const InputEvent = (event)=>{
        const {name,value} = event.target;
        setNote((prevData)=>{
            return {
                ...prevData,
                [name]: value
            }
        })
    }
    const addEvent = ()=>{
        props.passNote(note);
        setNote({
            title: "",
            content: ""
        });
    }
    const expandIt = ()=>{
        setExpand(true);
    }
    return (
        <>
        <div className="create_note">
            <form>
                {expand?<input type="text" placeholder="Title" autoComplete="off" name="title" value={note.title} onChange={InputEvent} />: null }
                <br/>
                <textarea rows="4" cols="50" placeholder="Write a note..." name="content" value={note.content} onChange={InputEvent} onClick={expandIt}></textarea>
                {expand? <Button className="btn" onClick={addEvent}>
                    <AddIcon/>
                </Button>:null}
            </form>
        </div>
        </>
    )
}
export default CreateNote;
