import React from 'react';
import { useState } from 'react';

const Contact = ()=>{
    const [email,setEmail] = useState();
    const [name,setName] = useState();
    const [phone,setPhone] = useState();
    const [msg,setMsg] = useState();
    const submit = ()=>{
        alert(`Your name is ${name} , Your email Id is ${email} , Your phone number is ${phone} `)
    }
    return (
        <>
        <div className="my-3">
            <h1 className="text-center display-3">Contact Us</h1>
            <div className="container contact_div">
                <div className="row">
                    <div className="col-md-6 col-md-6 col-10 mx-auto">
                        <form>
                            <div className="mb-3">
                            <label for="exampleFormControlInput1" className="form-label">Email address</label>
                            <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="name@example.com" value={setEmail(email)} onChange={(e)=>{setEmail(e.target.value)}} />
                            </div>
                            <div className="mb-3">
                            <label for="exampleFormControlInput1" className="form-label">Full Name</label>
                            <input type="text" className="form-control" id="exampleFormControlInput1" placeholder="Enter your name" value={setName(name)} onChange={(e)=>{setName(e.target.value)}} />
                            </div>
                            <div className="mb-3">
                            <label for="exampleFormControlInput1" className="form-label">Phone Number</label>
                            <input type="number" className="form-control" id="exampleFormControlInput1" placeholder="Enter your phone number" value={setPhone(phone)} onChange={(e)=>{setPhone(e.target.value)}} />
                            </div>
                            <div className="mb-3">
                            <label for="exampleFormControlTextarea1" className="form-label">Message</label>
                            <textarea className="form-control" id="exampleFormControlTextarea1" rows="3" value={setMsg(msg)} onChange={(e)=>{setMsg(e.target.value)}}></textarea>
                            </div>
                            <div className="col-12">
                            <button className="btn btn-outline-primary" type="submit" onClick={submit}>Submit form</button>
                            </div>
                        </form>

                    </div>

                </div>

            </div>
        </div>
        </>
    );
};
export default Contact;
