import React from 'react';
import { useState } from 'react';

const Contact = ()=>{
    const [data,setData] = useState({
        fullname:"",
        phone:"",
        email
    });
    const submit = ()=>{
        alert(`Your name is ${name} , Your email Id is ${email} , Your phone number is ${phone} , You typed ${msg}`);
    }
    return (
        <>
        <div className="my-3">
            <h1 className="text-center display-3">Contact Us</h1>
            <div className="container contact_div">
                <div className="row">
                    <div className="col-md-6 col-md-6 col-10 mx-auto">
                        <form onSubmit={submit}>
                            <div className="mb-3">
                            <label for="exampleFormControlInput1" className="form-label">Email address</label>
                            <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="name@example.com" name="email" value={setEmail(email)} onChange={InputEvent} />
                            </div>
                            <div className="mb-3">
                            <label for="exampleFormControlInput1" className="form-label">Full Name</label>
                            <input type="text" className="form-control" id="exampleFormControlInput1" placeholder="Enter your name" name="fullname" value={setName(name)} onChange={InputEvent} />
                            </div>
                            <div className="mb-3">
                            <label for="exampleFormControlInput1" className="form-label">Phone Number</label>
                            <input type="number" className="form-control" id="exampleFormControlInput1" placeholder="Enter your phone number" name="phone" value={setPhone(phone)} onChange={InputEvent} />
                            </div>
                            <div className="mb-3">
                            <label for="exampleFormControlTextarea1" className="form-label">Message</label>
                            <textarea className="form-control" id="exampleFormControlTextarea1" rows="3" name="msg" value={setMsg(msg)} onChange={InputEvent}></textarea>
                            </div>
                            <div className="col-12">
                            <button className="btn btn-outline-primary" type="submit">Submit form</button>
                            </div>
                        </form>

                    </div>

                </div>

            </div>
        </div>
        </>
    );
};
export default Contact;
